import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TransactionList = () => {
  const [transactions, setTransactions] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setTransactions(res.data);
      } catch (err) {
        setError(err.response ? err.response.data.msg : 'Something went wrong');
      }
    };

    fetchTransactions();
  }, []);

  return (
    <div>
      <h1>Transaction History</h1>
      <ul>
        {transactions.map((transaction) => (
          <li key={transaction._id}>
            <p>Added by User(id): {transaction.user}</p>
            <p>Billed: {transaction.amountBilled}</p>
            <p>Paid: {transaction.amountPaid}</p>
            <p>Balance: {transaction.balance}</p>
            <p>Debt: {transaction.debt}</p>
            <p>Date: {new Date(transaction.date).toLocaleString()}</p>
          </li>
        ))}
      </ul>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default TransactionList;
