import React, { useState } from 'react';
import axios from 'axios';

const AddTransaction = () => {
  const [amountBilled, setAmountBilled] = useState('');
  const [amountPaid, setAmountPaid] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Преобразование строк в числа
    const billed = Number(amountBilled);
    const paid = Number(amountPaid);

    // Проверка на валидность чисел
    if (isNaN(billed) || isNaN(paid)) {
      alert('Пожалуйста, введите корректные числовые значения.');
      return;
    }

    const transaction = { amountBilled: billed, amountPaid: paid };

    try {
      await axios.post('http://localhost:5000/api/transaction',
        transaction,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

      setSuccess('Transaction added successfully');
      setAmountBilled('');
      setAmountPaid('');
    } catch (err) {
      setError(err.response ? err.response.data.msg : 'Something went wrong');
    }
  };

  return (
    <div>
      <h1>Add Transaction</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Amount Billed</label>
          <input
            type="number"
            name='amountBilled'
            value={amountBilled}
            onChange={(e) => setAmountBilled(e.target.value)}
            required
            min="0"
            step="0.01"
          />
        </div>
        <div>
          <label>Amount Paid</label>
          <input
            type="number"
            name='amountPaid'
            value={amountPaid}
            onChange={(e) => setAmountPaid(e.target.value)}
            required
            min="0"
            step="0.01"
          />
        </div>
        <button type="submit">Add Transaction</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
    </div>
  );
};

export default AddTransaction;
