import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams, Link } from 'react-router-dom';
import { Box, Button, TextField, Typography, Alert, List, ListItem } from '@mui/material';

const PostForm = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [error, setError] = useState('');
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPost = async () => {
      if (id) {
        try {
          const res = await axios.get(`http://localhost:5000/api/posts/${id}`, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          });
          setTitle(res.data.title);
          setContent(res.data.content);
        } catch (err) {
          console.error('Error fetching post:', err);
          setError(err.response ? err.response.data.msg : 'Something went wrong');
        }
      }
    };

    fetchPost();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const postData = { title, content };

    try {
      if (id) {
        await axios.put(`http://localhost:5000/api/posts/${id}`, postData, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
      } else {
        await axios.post('http://localhost:5000/api/posts', postData, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
      }

      navigate('/posts');
    } catch (err) {
      console.error('Error saving post:', err);
      setError(err.response ? err.response.data.msg : 'Something went wrong');
    }
  };

  return (
    <Box sx={{ maxWidth: 600, margin: 'auto', padding: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', marginBottom: 2 }}>
        <Link to="/register">
          <Button variant="contained" color="primary">Register</Button>
        </Link>
        <Link to="/login">
          <Button variant="contained" color="primary">Login</Button>
        </Link>
        <Link to="/posts">
          <Button variant="contained" color="primary">Новости</Button>
        </Link>
      </Box>

      <Typography variant="h4" gutterBottom>История транзакций</Typography>

      <Box mb={3}>
        <Typography variant="h6">Общая сумма начисленных средств: {totalAmountBilled}</Typography>
      </Box>

      <Box mb={3}>
        <Typography variant="h6">Сальдо пользователей</Typography>
        <List>
          {userBalances.map((balance, index) => (
            <ListItem key={index}>
              <Typography>Пользователь: {balance.user}, Сальдо: {balance.balance}</Typography>
            </ListItem>
          ))}
        </List>
      </Box>

      <Typography variant="h4" gutterBottom>
        {id ? 'Edit Post' : 'Create Post'}
      </Typography>
      
      {error && (
        <Alert severity="error" sx={{ marginBottom: 2 }}>
          {error}
        </Alert>
      )}
      
      <form onSubmit={handleSubmit}>
        <TextField
          label="Title"
          variant="outlined"
          fullWidth
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
          sx={{ marginBottom: 2 }}
        />
        <TextField
          label="Content"
          variant="outlined"
          fullWidth
          multiline
          rows={4}
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
          sx={{ marginBottom: 2 }}
        />
        <Button type="submit" variant="contained" color="primary">
          {id ? 'Update' : 'Create'}
        </Button>
      </form>
    </Box>
  );
};

export default PostForm;
