import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function EditTransaction() {
  const { id } = useParams(); // Получаем ID транзакции из параметров URL
  const [transaction, setTransaction] = useState(null);
  const [error, setError] = useState(null);

  console.log(id);
  

  useEffect(() => {
    const fetchTransaction = async () => {
      try {
        const token = localStorage.getItem('token'); // Получаем токен авторизации

        if (!token) {
          setError('Отсутствует токен авторизации');
          return;
        }

        // Проверяем, есть ли ID и выводим его для проверки
        if (!id) {
          setError('ID транзакции отсутствует');
          console.error('ID транзакции отсутствует');
          return;
        }

        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };

        const response = await axios.get(`/api/transaction/${id}`, config); // Запрос к серверу по ID транзакции
        setTransaction(response.data);
      } catch (error) {
        console.error('Ошибка при загрузке транзакции:', error);
        setError('Ошибка при загрузке транзакции');
      }
    };

    fetchTransaction();
  }, [id]); // Используем ID как зависимость для useEffect

  if (error) {
    return <p>{error}</p>;
  }

  if (!transaction) {
    return <p>Загрузка...</p>;
  }

  return (
    <div>
      <h2>Редактировать транзакцию</h2>
      <p>Сумма счета: {transaction.amountBilled}</p>
      <p>Оплачено: {transaction.amountPaid}</p>
      {/* Добавьте здесь форму для редактирования */}
    </div>
  );
}

export default EditTransaction;
