import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TransactionItem from './TransactionItem';
import EditTransaction from './EditTransaction';

const TransactionList = () => {
  const [transactions, setTransactions] = useState([]);
  const [editingTransaction, setEditingTransaction] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setTransactions(res.data);
      } catch (err) {
        setError(err.response ? err.response.data.msg : 'Something went wrong');
      }
    };
    fetchTransactions();
  }, []);
  const handleDelete = () => {
    // После удаления обновляем список
    axios.get('/api/transactions')
      .then(response => setTransactions(response.data))
      .catch(error => console.error('Ошибка при загрузке транзакций:', error));
  };
  const handleUpdate = () => {
    // После редактирования обновляем список
    axios.get('/api/transactions')
      .then(response => setTransactions(response.data))
      .catch(error => console.error('Ошибка при загрузке транзакций:', error));
    setEditingTransaction(null); // Скрываем форму редактирования после обновления
  };

  return (
    <div>
      <h1>История транзакций</h1>
      <ul>
        <h2>Общие начисления</h2>
        {transactions.map((transaction) => (
          <li key={transaction._id}>
            <p>Добавлено пользователем(id): {transaction.user}</p>
            <p>Начислено: {transaction.amountBilled}</p>
            <p>Оплачено: {transaction.amountPaid}</p>
            <p>Сальдо: {transaction.balance}</p>
            <p>Долг: {transaction.debt}</p>
            <p>Дата и Время: {new Date(transaction.date).toLocaleString()}</p>
            <TransactionItem transaction={transaction} onDelete={handleDelete} />
          <button onClick={() => setEditingTransaction(transaction._id)}>Редактировать</button>
          </li>
        ))}
      </ul>
      <ul>
        <h2>Начисления №1</h2>
        {transactions.map((transaction) => (
          <li key={transaction._id}>
            <p>Добавлено пользователем(id): {transaction.user}</p>
            <p>Начислено: {transaction.sharebilled}</p>
            <p>Оплачено: {transaction.amountPaid}</p>
            {/* <p>Сальдо: {transaction.balance}</p> */}
            {/* <p>Долг: {transaction.debt}</p> */}
            <p>Дата и Время: {new Date(transaction.date).toLocaleString()}</p>
            <TransactionItem transaction={transaction} onDelete={handleDelete} />
          <button onClick={() => setEditingTransaction(transaction._id)}>Редактировать</button>
          </li>
        ))}
      </ul>
      <ul>
        <h2>Начисления №2</h2>
        {transactions.map((transaction) => (
          <li key={transaction._id}>
            <p>Добавлено пользователем(id): {transaction.user}</p>
            <p>Начислено: {transaction.sharebilled}</p>
            <p>Оплачено: {transaction.amountPaid}</p>
            {/* <p>Сальдо: {transaction.balance}</p> */}
            {/* <p>Долг: {transaction.debt}</p> */}
            <p>Дата и Время: {new Date(transaction.date).toLocaleString()}</p>
            <TransactionItem transaction={transaction} onDelete={handleDelete} />
          <button onClick={() => setEditingTransaction(transaction._id)}>Редактировать</button>
          </li>
        ))}
      </ul>
      <hr></hr>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default TransactionList;
