import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import axios from 'axios';
import TransactionItem from './TransactionItem';
import EditTransaction from './EditTransaction';

const TransactionList = () => {
  const [transactions, setTransactions] = useState([]);
  const [totalAmountBilled, setTotalAmountBilled] = useState(0);  // Для хранения общей суммы начисленных средств
  const [totalPaid, setTotalPaid] = useState([]);  // Для хранения общей суммы оплаченных средств
  const [eachOneBill, seteachOneBill] = useState(0);  // Для хранения общей суммы начисленных средств  
  const [editingTransactionId, setEditingTransactionId] = useState(null);
  const [error, setError] = useState('');

  // Функция для получения списка транзакций
  const fetchTransactions = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/transactions', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      // Устанавливаем транзакции и другие данные в состояние
      setTransactions(res.data.transactions);  // Устанавливаем список транзакций
      setTotalAmountBilled(res.data.totalAmountBilled[0]?.total || 0);  // Устанавливаем общую сумму начисленных средств
      setTotalPaid(res.data.totalPaid);  // Устанавливаем данные об оплатах
      seteachOneBill(res.data.eachOneBill);
      console.log(seteachOneBill(res.data.eachOneBill));
      

    } catch (err) {
      setError(err.response ? err.response.data.msg : 'Something went wrong');
    }
  };

  useEffect(() => {
    fetchTransactions(); // Вызываем получение транзакций при загрузке компонента
  }, []);

  // Обработчик удаления транзакции
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/transaction/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      
      // Удаляем транзакцию из состояния после успешного удаления
      setTransactions(transactions.filter(transaction => transaction._id !== id));
      // alert('Транзакция успешно удалена');
    } catch (error) {
      console.error('Ошибка при удалении транзакции:', error);
      setError('Ошибка при удалении транзакции');
    }
  };

  const handleEditClick = (id) => {
    setEditingTransactionId(id); // Устанавливаем ID для редактирования
  };

  // Функция для обновления транзакций после редактирования
  const handleUpdate = async () => {
    await fetchTransactions(); // Обновляем список транзакций после изменения
    setEditingTransactionId(null); // Сбрасываем редактируемую транзакцию
  };

  return (
    <div>
      <div>
        <Link to="/register">
          <button type="submit">Register</button>
        </Link>
      </div>
      <div>
        <Link to="/login">
          <button type="submit">Login</button>
        </Link>
      </div>
      <div>
        <Link to="/posts">
          <button type="submit">Новости</button>
        </Link>
      </div>
      <div>
        <Link to="/transactions">
          <button type="submit">ТНС</button>
        </Link>
      </div>
      <h1>История транзакций</h1>
      
      {/* Отображение общей суммы начисленных и оплаченных средств */}
      <div>
  <p>Общая сумма начисленных средств: {totalAmountBilled}</p>
  <p>Сумма задолжности: {eachOneBill}</p>
  <p>Общая сумма оплаченных средств:</p>
  <ul>
    {totalPaid.map((userPayment, index) => (
      <li key={index}>
        {console.log(userPayment)}
        Пользователь: {userPayment?._id?.username || "Неизвестно"} / {userPayment?._id?._id || "Неизвестно"}, Оплачено: {userPayment?.total || 0}
      </li>
    ))}
  </ul>
</div>
      <hr />

      <ul>
        {transactions.map((transaction) => (
          <li key={transaction._id}>
            <p>ID транзакции: {transaction._id}</p>
            <p>Добавлено пользователем (id): {transaction.user.username} / {transaction.user._id}</p>
            <p>Начислено: {transaction.amountBilled}</p>
            <p>Оплачено: {transaction.amountPaid}</p>
            <p>Сальдо: {transaction.balance}</p>
            <p>Долг: {transaction.debt}</p>
            <p>Дата и Время: {new Date(transaction.date).toLocaleString()}</p>            
            {/* Компонент для удаления транзакции */}
            <TransactionItem transaction={transaction} onDelete={() => handleDelete(transaction._id)} />

            {/* Кнопка редактирования */}
            <button onClick={() => handleEditClick(transaction._id)}>Редактировать</button>
          </li>
        ))}
      </ul>

      {/* Условное отображение формы редактирования */}
      {editingTransactionId && (
        <EditTransaction transactionId={editingTransactionId} onUpdate={handleUpdate} />
      )}

      {/* Вывод ошибок */}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default TransactionList;
