import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const PostForm = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [error, setError] = useState('');
  const { id } = useParams(); // Получение ID из URL
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      const fetchPost = async () => {
        try {
          const res = await axios.get(`http://localhost:5000/api/posts/${id}`, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          });
          setTitle(res.data.title);
          setContent(res.data.content);
        } catch (err) {
          console.error('Error fetching post:', err);
          setError(err.response ? err.response.data.msg : 'Something went wrong'); // Сохраняем сообщение об ошибке в состоянии
        }
      };

      fetchPost();
    }
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const postData = { title, content };

    try {
      if (id) {
        // Обновление поста
        await axios.put(`http://localhost:5000/api/posts/${id}`, postData, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
      } else {
        // Создание нового поста
        await axios.post('http://localhost:5000/api/posts', postData, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
      }

      navigate('/posts');
    } catch (err) {
      console.error('Error saving post:', err);
      alert('Failed to save the post. Please check the console for more details.');
      setError(err.response ? err.response.data.msg : 'Something went wrong'); // Сохраняем сообщение об ошибке в состоянии
    }
  };

  return (
    <div>
      <h1>{id ? 'Edit Post' : 'Create Post'}</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Content</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
          ></textarea>
        </div>
        <button type="submit">{id ? 'Update' : 'Create'}</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Отображение сообщения об ошибке */}
    </div>
  );
};

export default PostForm;
