import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function EditTransaction() {
  const { id } = useParams(); // Получаем ID транзакции из параметров URL
  const [transaction, setTransaction] = useState(null);
  const [error, setError] = useState(null);
  const [updatedTransaction, setUpdatedTransaction] = useState({});
  const navigate = useNavigate(); // Для навигации после обновления

  useEffect(() => {
    const fetchTransaction = async () => {
      try {
        const token = localStorage.getItem('token');

        if (!token) {
          setError('Отсутствует токен авторизации');
          return;
        }

        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };

        const response = await axios.get(`/api/transaction/${id}`, config);
        setTransaction(response.data);
        setUpdatedTransaction(response.data); // Инициализируем данные для редактирования
      } catch (error) {
        console.error('Ошибка при загрузке транзакции:', error);
        setError('Ошибка при загрузке транзакции');
      }
    };

    fetchTransaction();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedTransaction(prev => ({ ...prev, [name]: value })); // Обновляем данные для редактирования
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Предотвращаем перезагрузку страницы
    try {
      const token = localStorage.getItem('token');

      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };

      await axios.put(`/api/transaction/${id}`, updatedTransaction, config);
      // Перенаправляем пользователя после успешного обновления
      navigate('/transactions'); // Замените на нужный путь
    } catch (error) {
      console.error('Ошибка при обновлении транзакции:', error);
      setError('Ошибка при обновлении транзакции');
    }
  };

  if (error) {
    return <p>{error}</p>;
  }

  if (!transaction) {
    return <p>Загрузка...</p>;
  }

  return (
    <div>
      <h2>Редактировать транзакцию</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Сумма счета:</label>
          <input
            type="number"
            name="amountBilled"
            value={updatedTransaction.amountBilled || ''}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Оплачено:</label>
          <input
            type="number"
            name="amountPaid"
            value={updatedTransaction.amountPaid || ''}
            onChange={handleChange}
            required
          />
        </div>
        {/* Добавьте другие поля формы по мере необходимости */}
        <button type="submit">Сохранить изменения</button>
      </form>
    </div>
  );
}

export default EditTransaction;
