import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams, Link } from 'react-router-dom';
import { Box, Button, TextField, Typography, Alert } from '@mui/material';

const PostForm = ({ isAuthenticated }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [error, setError] = useState('');
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      const fetchPost = async () => {
        try {
          const res = await axios.get(`http://localhost:5000/api/posts/${id}`, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          });
          setTitle(res.data.title);
          setContent(res.data.content);
        } catch (err) {
          console.error('Error fetching post:', err);
          setError(err.response ? err.response.data.msg : 'Something went wrong');
        }
      };

      fetchPost();
    }
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const postData = { title, content };

    try {
      if (id) {
        // Обновление поста
        await axios.put(`http://localhost:5000/api/posts/${id}`, postData, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
      } else {
        // Создание нового поста
        await axios.post('http://localhost:5000/api/posts', postData, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
      }

      navigate('/posts');
    } catch (err) {
      console.error('Error saving post:', err);
      setError(err.response ? err.response.data.msg : 'Something went wrong');
    }
  };

  return (
    <>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', marginTop: 2 }}>
        <Button component={Link} to="/" color="primary">
          Главная
        </Button>
        <Button component={Link} to={isAuthenticated ? "/logout" : "/register"} color="primary">
          {isAuthenticated ? 'Выйти' : 'Регистрация'}
        </Button>
        <Button component={Link} to="/posts" color="primary">
          Новости
        </Button>
        <Button component={Link} to="/transactions" color="primary">
          ТНС
        </Button>
        <Button component={Link} to="/tgks" color="primary">
          ТГК
        </Button>
        <Button component={Link} to="/waters" color="primary">
          Водоканал
        </Button>
      </Box>
      
      <Box sx={{ maxWidth: 600, margin: 'auto', padding: 4 }}>
        <Typography variant="h4" gutterBottom>
          {id ? 'Edit Post' : 'Create Post'}
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ marginBottom: 2 }}>
            {error}
          </Alert>
        )}
        
        <form onSubmit={handleSubmit}>
          <TextField
            label="Title"
            variant="outlined"
            fullWidth
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            sx={{ marginBottom: 2 }}
          />
          <TextField
            label="Content"
            variant="outlined"
            fullWidth
            multiline
            rows={4}
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
            sx={{ marginBottom: 2 }}
          />
          <Button type="submit" variant="contained" color="primary">
            {id ? 'Update' : 'Create'}
          </Button>
        </form>
      </Box>
    </>
  );
};

export default PostForm;
