import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import TransactionItem from './TransactionItem';
import EditTransaction from './EditTransaction';

const TransactionList = () => {
  const [transactions, setTransactions] = useState([]);
  const [transactionId, setTransactionId] = useState(null); // Для хранения ID транзакции для редактирования
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setTransactions(res.data);
      } catch (err) {
        setError(err.response ? err.response.data.msg : 'Something went wrong');
      }
    };
    fetchTransactions();
  }, []);

  const handleDelete = async () => {
    // После удаления обновляем список
    axios.get('http://localhost:5000/api/transactions', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
      .then(response => setTransactions(response.data))
      .catch(error => console.error('Ошибка при загрузке транзакций:', error));
  };

  const handleEditClick = (id) => {
    setTransactionId(id); // Устанавливаем ID для редактирования
  };

  const handleUpdate = async () => {
    // Функция для обновления списка после редактирования
    const res = await axios.get('http://localhost:5000/api/transactions', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    });
    setTransactions(res.data);
    setTransactionId(null); // Сбрасываем ID редактируемой транзакции
  };

  return (
    <div>
      <h1>История транзакций</h1>
      <ul>
        <h2>Общие начисления</h2>
        {transactions.map((transaction) => (
          <li key={transaction._id}>
            <p>transaction._id {transaction._id}</p>
            <p>Добавлено пользователем(id): {transaction.user}</p>
            <p>Начислено: {transaction.amountBilled}</p>
            <p>Оплачено: {transaction.amountPaid}</p>
            <p>Сальдо: {transaction.balance}</p>
            <p>Долг: {transaction.debt}</p>
            <p>Дата и Время: {new Date(transaction.date).toLocaleString()}</p>
            <TransactionItem transaction={transaction} onDelete={handleDelete} />
            <button onClick={() => handleEditClick(transaction._id)}>Редактировать</button>
          </li>
        ))}
      </ul>

      {/* Условно отображаем компонент редактирования */}
      {transactionId && (
        <EditTransaction transactionId={transactionId} onUpdate={handleUpdate} />
      )}

      <hr />
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default TransactionList;
