import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const AuthForm = ({ isRegister }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = isRegister
        ? await axios.post('http://localhost:5000/api/users/register', { username, email, password })
        : await axios.post('http://localhost:5000/api/users/login', { email, password });
      
      localStorage.setItem('token', response.data.token);
      navigate('/posts');
    } catch (err) {
      setError(err.response ? err.response.data.msg : 'Something went wrong'); // Сохраняем сообщение об ошибке в состоянии
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{isRegister ? 'Register' : 'Login'}</h2>
      {isRegister && (
        <div>
          <label>Username</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
      )}
      <div>
        <label>Email</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <div>
        <label>Password</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      <div>
        <Link to="/register">
          <button type="submit">Register</button>
        </Link>
      </div>
      <div>
        <Link to="/login">
          <button type="submit">Login</button>
        </Link>
      </div>
      <div>
        <Link to="/posts">
          <button type="submit">Новости</button>
        </Link>
      </div>
      <div>
        <Link to="/transactions">
          <button type="submit">ТНС</button>
        </Link>
      </div>
      <button type="submit">{isRegister ? 'Register' : 'Login'}</button>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Отображение сообщения об ошибке */}
    </form>
  );
};

export default AuthForm;
