import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { Box, TextField, Button, Typography, Alert } from '@mui/material';

const AuthForm = ({ isRegister }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = isRegister
        ? await axios.post('http://localhost:5000/api/users/register', { username, email, password })
        : await axios.post('http://localhost:5000/api/users/login', { email, password });
      
      localStorage.setItem('token', response.data.token);
      navigate('/posts');
    } catch (err) {
      setError(err.response ? err.response.data.msg : 'Something went wrong');
    }
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      sx={{
        display: 'flex',
        flexDirection: 'column',
        gap: 2,
        maxWidth: 400,
        margin: '0 auto',
        padding: 2,
      }}
    >
      <Typography variant="h4" align="center">
        {isRegister ? 'Register' : 'Login'}
      </Typography>

      {isRegister && (
        <TextField
          label="Username"
          variant="outlined"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          fullWidth
        />
      )}

      <TextField
        label="Email"
        type="email"
        variant="outlined"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        fullWidth
        required
      />

      <TextField
        label="Password"
        type="password"
        variant="outlined"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        fullWidth
        required
      />

      <Button variant="contained" color="primary" type="submit" fullWidth>
        {isRegister ? 'Register' : 'Login'}
      </Button>

      {error && (
        <Alert severity="error">
          {error}
        </Alert>
      )}

      <Box sx={{ display: 'flex', justifyContent: 'space-between', marginTop: 2 }}>
        <Button component={Link} to="/register" color="primary">
          Register
        </Button>
        <Button component={Link} to="/login" color="primary">
          Login
        </Button>
        <Button component={Link} to="/posts" color="primary">
          Новости
        </Button>
        <Button component={Link} to="/transactions" color="primary">
          ТНС
        </Button>
      </Box>
    </Box>
  );
};

export default AuthForm;
