import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function EditTransaction({ transactionId, onUpdate }) {
  const { id } = useParams(); // Получаем ID транзакции из параметров URL
  const [amountBilled, setAmountBilled] = useState('');
  const [amountPaid, setAmountPaid] = useState('');

  useEffect(() => {
    // Получаем данные транзакции по ID
    axios.get(`/api/transaction/${transactionId}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
      .then(response => {
        const { amountBilled, amountPaid } = response.data;
        setAmountBilled(amountBilled);
        setAmountPaid(amountPaid);
      })
      .catch(error => console.error('Ошибка при загрузке транзакции:', error));
  }, [transactionId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/api/transaction/${transactionId}`, { amountBilled, amountPaid });
      alert('Транзакция успешно обновлена');
      onUpdate(); // Вызов обновления списка транзакций
    } catch (error) {
      console.error('Ошибка при редактировании транзакции:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Сумма счета:</label>
        <input
          type="number"
          value={amountBilled}
          onChange={(e) => setAmountBilled(e.target.value)}
        />
      </div>
      <div>
        <label>Оплаченная сумма:</label>
        <input
          type="number"
          value={amountPaid}
          onChange={(e) => setAmountPaid(e.target.value)}
        />
      </div>
      <button type="submit">Обновить транзакцию</button>
    </form>
  );
}

export default EditTransaction;
